const initialState = {
    user: null,
    isAuthenticated: true
}

const UserReducer = (state = initialState, action) => {
    switch(action.type) {
        case "LOGIN": return {
            user: action.user,
            isAuthenticated: true
        }
        case "LOGOUT": return {
            user: null,
            isAuthenticated: false
        }
        default: return state
    }
}

export default UserReducer
