import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { alpha, makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button'
import ButtonGroup from '@material-ui/core/ButtonGroup'
import Modal from '@material-ui/core/Modal'

import { useSelector } from "react-redux"
import { useState } from "react"

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));

export default function Header() {
    
    const state = useSelector((state) => state)
    console.log(state.UserReducer)

    const classes = useStyles();

    const [loginModal, setLoginModal] = useState(false)

    const handleClose = () => {
        setLoginModal(false)
    }
    return (
        <div className={classes.root}>

        <Modal open={loginModal}
                onClose={handleClose}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description">
                HOLA
        </Modal>

        <AppBar position="static" style={{backgroundColor:"#171717"}}>
            <Toolbar>
            <Typography className={classes.title} variant="h6" noWrap>
                AUTH-SYSTEM
            </Typography>
            <div className={classes.search}>
                {
                    state.UserReducer.isAuthenticated ? 
                    <>
                        <Button color="inherit" style={{backgroundColor:"#444444",}}>
                            LOGOUT
                        </Button>
                    </> :
                    <ButtonGroup disableElevation variant="contained">
                        <Button color="inherit" style={{backgroundColor:"#DA0037",}} onClick={() => setLoginModal(true)}>
                            LOGIN
                        </Button>
                        
                        <Button color="inherit" style={{backgroundColor:"#444444",}}>
                            SIGNIN
                        </Button>
                    </ButtonGroup>
                }
            </div>
            </Toolbar>
        </AppBar>
        </div>
    );
}
